#!/bin/bash\n# ORACLE 24/7 Monitoring\n# Tracks: Memory, CPU, Errors, Performance, Edge Cases\n\necho \"üîÆ ORACLE Stability Monitoring (24/7)\"\necho \"=====================================\"\necho \"Monitoring started: $(date)\"\necho \"\"\n\n# Monitoring loop (every 60 seconds)\nwhile true; do\n  timestamp=$(date '+%Y-%m-%d %H:%M:%S')\n  \n  # 1. Check if application is running\n  if ! curl -s http://localhost:8000/health > /dev/null 2>&1; then\n    echo \"[$timestamp] ‚ùå ERROR: Application not responding\"\n    break\n  fi\n  \n  # 2. Get memory & CPU\n  ps_info=$(ps aux | grep \"python3 main.py\" | grep -v grep | awk '{print $3, $4}')\n  if [ ! -z \"$ps_info\" ]; then\n    cpu=$(echo \"$ps_info\" | awk '{print $1}')\n    mem=$(echo \"$ps_info\" | awk '{print $2}')\n    echo \"[$timestamp] CPU: ${cpu}% | Memory: ${mem}% | Status: ‚úÖ\"\n  fi\n  \n  # 3. Check for errors in logs\n  errors=$(tail -5 oracle.log 2>/dev/null | grep -c \"ERROR\")\n  if [ \"$errors\" -gt 0 ]; then\n    echo \"[$timestamp] ‚ö†Ô∏è  Errors detected in last 5 lines\"\n    tail -5 oracle.log\n  fi\n  \n  # 4. API health\n  health=$(curl -s http://localhost:8000/health | jq -r '.status' 2>/dev/null)\n  echo \"[$timestamp] Health: $health\"\n  \n  # 5. Database check\n  psql oracle -c \"SELECT COUNT(*) FROM users;\" > /dev/null 2>&1\n  if [ $? -eq 0 ]; then\n    user_count=$(psql oracle -c \"SELECT COUNT(*) FROM users;\" -t 2>/dev/null | xargs)\n    msg_count=$(psql oracle -c \"SELECT COUNT(*) FROM messages;\" -t 2>/dev/null | xargs)\n    echo \"[$timestamp] DB: Users=$user_count Messages=$msg_count\"\n  else\n    echo \"[$timestamp] ‚ö†Ô∏è  Database connection issue\"\n  fi\n  \n  sleep 60\ndone\n"